// Extract webhook payload
const payload = $input.all()[0].json;

const appId = payload.app_id || 'unknown';
const userId = payload.user_id || 'unknown';
const framework = payload.framework || 'unknown';
const provider = payload.provider || 'unknown';
const filesCount = payload.files_count || 0;
const requiresDatabase = payload.requires_database || false;
const generatedAt = payload.generated_at || new Date().toISOString();

// Format Slack message
const message = {
  text: `üéâ New App Generated!`,
  blocks: [
    {
      type: "header",
      text: {
        type: "plain_text",
        text: "üöÄ NexusAI Generated New App"
      }
    },
    {
      type: "section",
      fields: [
        {
          type: "mrkdwn",
          text: `*App ID:*\n${appId}`
        },
        {
          type: "mrkdwn",
          text: `*User:*\n${userId}`
        },
        {
          type: "mrkdwn",
          text: `*Framework:*\n${framework}`
        },
        {
          type: "mrkdwn",
          text: `*AI Provider:*\n${provider}`
        },
        {
          type: "mrkdwn",
          text: `*Files:*\n${filesCount}`
        },
        {
          type: "mrkdwn",
          text: `*Database:*\n${requiresDatabase ? '‚úÖ Yes' : '‚ùå No'}`
        }
      ]
    },
    {
      type: "context",
      elements: [
        {
          type: "mrkdwn",
          text: `Generated at: ${generatedAt}`
        }
      ]
    },
    {
      type: "actions",
      elements: [
        {
          type: "button",
          text: {
            type: "plain_text",
            text: "View App"
          },
          url: `https://chatbuilds.com/app/${appId}`,
          style: "primary"
        },
        {
          type: "button",
          text: {
            type: "plain_text",
            text: "Deploy Now"
          },
          url: `https://chatbuilds.com/deploy/${appId}`,
          style: "primary"
        }
      ]
    }
  ]
};

return [{
  json: {
    message,
    app_id: appId,
    user_id: userId,
    framework,
    provider
  }
}];
